import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/news');
        const data = await response.json();
         // Check if the 'articles' key exists and is an array
         if (data.articles && Array.isArray(data.articles)) {
          setArticles(data.articles);
        } else {
          console.error('Invalid data structure:', data);
          setArticles([]); // Fallback to an empty array
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching news:', error);
        setLoading(false);
      }
    };

    fetchNews();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Top Headlines</h1>
      </header>
      <main className="News-container">
        {loading ? (
          <p>Loading news...</p>
        ) : articles.length > 0 ? (
          articles.map((article, index) => (
            <div className="News-card" key={index}>
              <img src={article.urlToImage} alt={article.title} className="News-image" />
              <h2 className="News-title">{article.title}</h2>
              <p className="News-source">{article.source.name}</p>
              <p className="News-date">{new Date(article.publishedAt).toLocaleDateString()}</p>
              <a href={article.url} target="_blank" rel="noopener noreferrer" className="News-link">Read more</a>
            </div>
          ))
        ) : (
          <p>No articles found.</p>
        )}
      </main>
    </div>
  );
}

export default App;
